contracts/BatchPrediction.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title BatchPrediction
 * @notice Allows users to place multiple predictions across different markets in a single transaction
 * @dev This contract acts as a router to batch multiple buyShares calls
 */

interface IPredictionMarket {
    function buyShares(uint8 optionIndex) external payable;
    function isOpenForBetting() external view returns (bool);
}

contract BatchPrediction {
    // Struct to hold prediction data
    struct Prediction {
        address market;      // Market contract address
        uint8 optionIndex;   // Option index to bet on
        uint256 amount;      // Amount of MNT to bet
    }

    // Events
    event BatchPredictionExecuted(
        address indexed user,
        uint256 totalAmount,
        uint256 marketsCount
    );

    event PredictionPlaced(
        address indexed user,
        address indexed market,
        uint8 optionIndex,
        uint256 amount
    );

    event PredictionFailed(
        address indexed user,
        address indexed market,
        uint8 optionIndex,
        uint256 amount,
        string reason
    );

    // Owner for emergency functions
    address public owner;

    constructor() {
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    /**
     * @notice Execute multiple predictions in a single transaction
     * @param predictions Array of Prediction structs containing market, option, and amount
     * @dev The total msg.value must equal the sum of all prediction amounts
     */
    function batchPredict(Prediction[] calldata predictions) external payable {
        require(predictions.length > 0, "No predictions provided");
        require(predictions.length <= 20, "Too many predictions (max 20)");

        uint256 totalRequired = 0;
        for (uint256 i = 0; i < predictions.length; i++) {
            totalRequired += predictions[i].amount;
        }
        require(msg.value >= totalRequired, "Insufficient MNT sent");

        uint256 successCount = 0;
        uint256 totalSpent = 0;

        for (uint256 i = 0; i < predictions.length; i++) {
            Prediction calldata pred = predictions[i];

            // Skip if amount is 0
            if (pred.amount == 0) continue;

            // Check if market is valid and open
            try IPredictionMarket(pred.market).isOpenForBetting() returns (bool isOpen) {
                if (!isOpen) {
                    emit PredictionFailed(msg.sender, pred.market, pred.optionIndex, pred.amount, "Market not open");
                    continue;
                }
            } catch {
                emit PredictionFailed(msg.sender, pred.market, pred.optionIndex, pred.amount, "Invalid market");
                continue;
            }

            // Execute the prediction
            try IPredictionMarket(pred.market).buyShares{value: pred.amount}(pred.optionIndex) {
                emit PredictionPlaced(msg.sender, pred.market, pred.optionIndex, pred.amount);
                successCount++;
                totalSpent += pred.amount;
            } catch Error(string memory reason) {
                emit PredictionFailed(msg.sender, pred.market, pred.optionIndex, pred.amount, reason);
            } catch {
                emit PredictionFailed(msg.sender, pred.market, pred.optionIndex, pred.amount, "Unknown error");
            }
        }

        require(successCount > 0, "All predictions failed");

        emit BatchPredictionExecuted(msg.sender, totalSpent, successCount);

        // Refund excess MNT
        uint256 refund = msg.value - totalSpent;
        if (refund > 0) {
            (bool success, ) = payable(msg.sender).call{value: refund}("");
            require(success, "Refund failed");
        }
    }

    /**
     * @notice Execute multiple predictions with a simpler interface
     * @param markets Array of market addresses
     * @param optionIndices Array of option indices
     * @param amounts Array of amounts in wei
     */
    function batchPredictSimple(
        address[] calldata markets,
        uint8[] calldata optionIndices,
        uint256[] calldata amounts
    ) external payable {
        require(markets.length == optionIndices.length && markets.length == amounts.length, "Array length mismatch");
        require(markets.length > 0, "No predictions provided");
        require(markets.length <= 20, "Too many predictions (max 20)");

        Prediction[] memory predictions = new Prediction[](markets.length);
        for (uint256 i = 0; i < markets.length; i++) {
            predictions[i] = Prediction({
                market: markets[i],
                optionIndex: optionIndices[i],
                amount: amounts[i]
            });
        }

        // Calculate total required
        uint256 totalRequired = 0;
        for (uint256 i = 0; i < predictions.length; i++) {
            totalRequired += predictions[i].amount;
        }
        require(msg.value >= totalRequired, "Insufficient MNT sent");

        uint256 successCount = 0;
        uint256 totalSpent = 0;

        for (uint256 i = 0; i < predictions.length; i++) {
            Prediction memory pred = predictions[i];

            if (pred.amount == 0) continue;

            try IPredictionMarket(pred.market).isOpenForBetting() returns (bool isOpen) {
                if (!isOpen) {
                    emit PredictionFailed(msg.sender, pred.market, pred.optionIndex, pred.amount, "Market not open");
                    continue;
                }
            } catch {
                emit PredictionFailed(msg.sender, pred.market, pred.optionIndex, pred.amount, "Invalid market");
                continue;
            }

            try IPredictionMarket(pred.market).buyShares{value: pred.amount}(pred.optionIndex) {
                emit PredictionPlaced(msg.sender, pred.market, pred.optionIndex, pred.amount);
                successCount++;
                totalSpent += pred.amount;
            } catch Error(string memory reason) {
                emit PredictionFailed(msg.sender, pred.market, pred.optionIndex, pred.amount, reason);
            } catch {
                emit PredictionFailed(msg.sender, pred.market, pred.optionIndex, pred.amount, "Unknown error");
            }
        }

        require(successCount > 0, "All predictions failed");

        emit BatchPredictionExecuted(msg.sender, totalSpent, successCount);

        uint256 refund = msg.value - totalSpent;
        if (refund > 0) {
            (bool success, ) = payable(msg.sender).call{value: refund}("");
            require(success, "Refund failed");
        }
    }

    /**
     * @notice Validate predictions before execution (view function)
     * @param markets Array of market addresses
     * @param amounts Array of amounts
     * @return valid Array of booleans indicating if each market is valid and open
     * @return totalAmount Total amount required
     */
    function validatePredictions(
        address[] calldata markets,
        uint256[] calldata amounts
    ) external view returns (bool[] memory valid, uint256 totalAmount) {
        require(markets.length == amounts.length, "Array length mismatch");

        valid = new bool[](markets.length);
        totalAmount = 0;

        for (uint256 i = 0; i < markets.length; i++) {
            try IPredictionMarket(markets[i]).isOpenForBetting() returns (bool isOpen) {
                valid[i] = isOpen && amounts[i] > 0;
            } catch {
                valid[i] = false;
            }
            totalAmount += amounts[i];
        }
    }

    /**
     * @notice Emergency withdraw function for stuck funds
     */
    function emergencyWithdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No balance");
        (bool success, ) = payable(owner).call{value: balance}("");
        require(success, "Withdraw failed");
    }

    /**
     * @notice Transfer ownership
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Invalid address");
        owner = newOwner;
    }

    // Allow contract to receive MNT
    receive() external payable {}
}

contracts/interfaces/IOptimisticOracleV2.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title Simplified interface for UMA Optimistic Oracle V2
 * @notice Contains only the functions needed for prediction market resolution
 */
interface IOptimisticOracleV2 {
    // Struct to store request data
    struct Request {
        address proposer;
        address disputer;
        IERC20 currency;
        bool settled;
        bool refundOnDispute;
        int256 proposedPrice;
        int256 resolvedPrice;
        uint256 expirationTime;
        uint256 reward;
        uint256 finalFee;
        uint256 bond;
        uint256 customLiveness;
    }

    /**
     * @notice Requests a new price
     * @param identifier Price identifier
     * @param timestamp Timestamp of the price request
     * @param ancillaryData Additional data for the request (question text)
     * @param currency ERC20 token used for payment of rewards and fees
     * @param reward Reward offered to proposers
     * @return totalBond The bond currency amount required
     */
    function requestPrice(
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData,
        IERC20 currency,
        uint256 reward
    ) external returns (uint256 totalBond);

    /**
     * @notice Set custom liveness for a request
     * @param identifier Price identifier
     * @param timestamp Request timestamp
     * @param ancillaryData Request ancillary data
     * @param customLiveness Custom liveness in seconds
     */
    function setCustomLiveness(
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData,
        uint256 customLiveness
    ) external;

    /**
     * @notice Proposes a price value for an existing request
     * @param requester Address that made the original request
     * @param identifier Price identifier
     * @param timestamp Request timestamp
     * @param ancillaryData Request ancillary data
     * @param proposedPrice Proposed price value
     * @return totalBond The bond currency amount
     */
    function proposePrice(
        address requester,
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData,
        int256 proposedPrice
    ) external returns (uint256 totalBond);

    /**
     * @notice Settles a request after liveness period
     * @param requester Address that made the original request
     * @param identifier Price identifier
     * @param timestamp Request timestamp
     * @param ancillaryData Request ancillary data
     * @return payout The payout amount
     */
    function settle(
        address requester,
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData
    ) external returns (uint256 payout);

    /**
     * @notice Get the request data
     * @param requester Address that made the original request
     * @param identifier Price identifier
     * @param timestamp Request timestamp
     * @param ancillaryData Request ancillary data
     * @return request The request struct
     */
    function getRequest(
        address requester,
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData
    ) external view returns (Request memory);

    /**
     * @notice Check if request has resolved price
     * @param requester Address that made the original request
     * @param identifier Price identifier
     * @param timestamp Request timestamp
     * @param ancillaryData Request ancillary data
     * @return hasPrice Whether price has been resolved
     */
    function hasPrice(
        address requester,
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData
    ) external view returns (bool);

    /**
     * @notice Get state of the request
     * @param requester Address that made the original request
     * @param identifier Price identifier
     * @param timestamp Request timestamp
     * @param ancillaryData Request ancillary data
     * @return state The state of the request
     */
    function getState(
        address requester,
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData
    ) external view returns (uint8);
}

contracts/MockERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/// @title MockERC20
/// @notice Simple ERC20 token for testing purposes
contract MockERC20 {
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalSupply;

    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    constructor(string memory _name, string memory _symbol, uint8 _decimals) {
        name = _name;
        symbol = _symbol;
        decimals = _decimals;
    }

    /// @notice Mint tokens to an address (for testing)
    function mint(address to, uint256 amount) external {
        balanceOf[to] += amount;
        totalSupply += amount;
        emit Transfer(address(0), to, amount);
    }

    /// @notice Burn tokens from caller
    function burn(uint256 amount) external {
        require(balanceOf[msg.sender] >= amount, "Insufficient balance");
        balanceOf[msg.sender] -= amount;
        totalSupply -= amount;
        emit Transfer(msg.sender, address(0), amount);
    }

    /// @notice Transfer tokens
    function transfer(address to, uint256 amount) external returns (bool) {
        require(balanceOf[msg.sender] >= amount, "Insufficient balance");

        balanceOf[msg.sender] -= amount;
        balanceOf[to] += amount;

        emit Transfer(msg.sender, to, amount);
        return true;
    }

    /// @notice Approve spender
    function approve(address spender, uint256 amount) external returns (bool) {
        allowance[msg.sender][spender] = amount;
        emit Approval(msg.sender, spender, amount);
        return true;
    }

    /// @notice Transfer from (requires approval)
    function transferFrom(address from, address to, uint256 amount) external returns (bool) {
        require(balanceOf[from] >= amount, "Insufficient balance");
        require(allowance[from][msg.sender] >= amount, "Insufficient allowance");

        allowance[from][msg.sender] -= amount;
        balanceOf[from] -= amount;
        balanceOf[to] += amount;

        emit Transfer(from, to, amount);
        return true;
    }
}

contracts/mocks/MockOptimisticOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title MockOptimisticOracle
 * @notice Mock implementation of UMA Optimistic Oracle V2 for local testing
 */
contract MockOptimisticOracle {
    struct Request {
        address proposer;
        address disputer;
        IERC20 currency;
        bool settled;
        bool refundOnDispute;
        int256 proposedPrice;
        int256 resolvedPrice;
        uint256 expirationTime;
        uint256 reward;
        uint256 finalFee;
        uint256 bond;
        uint256 customLiveness;
    }

    // requester => identifier => timestamp => ancillaryData hash => Request
    mapping(address => mapping(bytes32 => mapping(uint256 => mapping(bytes32 => Request)))) public requests;

    // For testing: allow setting resolved prices
    mapping(bytes32 => int256) public mockPrices;

    uint256 public defaultLiveness = 30; // 30 seconds for testing

    event PriceRequested(
        address indexed requester,
        bytes32 identifier,
        uint256 timestamp,
        bytes ancillaryData
    );

    event PriceProposed(
        address indexed requester,
        address indexed proposer,
        bytes32 identifier,
        uint256 timestamp,
        bytes ancillaryData,
        int256 proposedPrice
    );

    event PriceSettled(
        address indexed requester,
        bytes32 identifier,
        uint256 timestamp,
        bytes ancillaryData,
        int256 price
    );

    function requestPrice(
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData,
        IERC20 currency,
        uint256 reward
    ) external returns (uint256) {
        bytes32 dataHash = keccak256(ancillaryData);

        requests[msg.sender][identifier][timestamp][dataHash] = Request({
            proposer: address(0),
            disputer: address(0),
            currency: currency,
            settled: false,
            refundOnDispute: false,
            proposedPrice: 0,
            resolvedPrice: 0,
            expirationTime: 0,
            reward: reward,
            finalFee: 0,
            bond: 0,
            customLiveness: defaultLiveness
        });

        emit PriceRequested(msg.sender, identifier, timestamp, ancillaryData);
        return 0;
    }

    function setCustomLiveness(
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData,
        uint256 customLiveness
    ) external {
        bytes32 dataHash = keccak256(ancillaryData);
        requests[msg.sender][identifier][timestamp][dataHash].customLiveness = customLiveness;
    }

    function proposePrice(
        address requester,
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData,
        int256 proposedPrice
    ) external returns (uint256) {
        bytes32 dataHash = keccak256(ancillaryData);
        Request storage request = requests[requester][identifier][timestamp][dataHash];

        request.proposer = msg.sender;
        request.proposedPrice = proposedPrice;
        request.expirationTime = block.timestamp + request.customLiveness;

        emit PriceProposed(requester, msg.sender, identifier, timestamp, ancillaryData, proposedPrice);
        return 0;
    }

    function settle(
        address requester,
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData
    ) external returns (uint256) {
        bytes32 dataHash = keccak256(ancillaryData);
        Request storage request = requests[requester][identifier][timestamp][dataHash];

        require(!request.settled, "Already settled");

        // For mock: use proposed price or mock price
        int256 finalPrice = request.proposedPrice;
        if (mockPrices[dataHash] != 0) {
            finalPrice = mockPrices[dataHash];
        }

        request.settled = true;
        request.resolvedPrice = finalPrice;

        emit PriceSettled(requester, identifier, timestamp, ancillaryData, finalPrice);
        return 0;
    }

    function getRequest(
        address requester,
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData
    ) external view returns (Request memory) {
        bytes32 dataHash = keccak256(ancillaryData);
        return requests[requester][identifier][timestamp][dataHash];
    }

    function hasPrice(
        address requester,
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData
    ) external view returns (bool) {
        bytes32 dataHash = keccak256(ancillaryData);
        return requests[requester][identifier][timestamp][dataHash].settled;
    }

    function getState(
        address requester,
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData
    ) external view returns (uint8) {
        bytes32 dataHash = keccak256(ancillaryData);
        Request memory request = requests[requester][identifier][timestamp][dataHash];

        if (request.settled) return 6; // Settled
        if (request.proposer != address(0) && block.timestamp >= request.expirationTime) return 4; // Expired (ready to settle)
        if (request.proposer != address(0)) return 2; // Proposed
        if (request.currency != IERC20(address(0))) return 1; // Requested
        return 0; // Invalid
    }

    // ============ Test Helpers ============

    /// @notice Set a mock price for testing (skips proposal process)
    function setMockPrice(bytes memory ancillaryData, int256 price) external {
        bytes32 dataHash = keccak256(ancillaryData);
        mockPrices[dataHash] = price;
    }

    /// @notice Fast-forward a request to settled state for testing
    function mockSettle(
        address requester,
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData,
        int256 price
    ) external {
        bytes32 dataHash = keccak256(ancillaryData);
        Request storage request = requests[requester][identifier][timestamp][dataHash];

        request.settled = true;
        request.resolvedPrice = price;
        request.proposer = msg.sender;
        request.proposedPrice = price;

        emit PriceSettled(requester, identifier, timestamp, ancillaryData, price);
    }

    /// @notice Set default liveness for new requests
    function setDefaultLiveness(uint256 _liveness) external {
        defaultLiveness = _liveness;
    }
}

contracts/PredictionMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

contract PredictionMarket {
    address public creator;
    address public resolver; // UMA resolver contract

    string public question;
    uint256 public endTime;
    uint256 public createdAt;

    enum Status { Active, Resolved, Cancelled }
    Status public marketStatus;

    // Custom options (2-4 options)
    uint8 public optionCount;
    string[] public optionLabels;
    bool[] public winningOptions;
    bool public hasWinners;

    // Shares per option: user => optionIndex => shares
    mapping(address => mapping(uint8 => uint256)) public shares;
    mapping(address => bool) public hasClaimed;

    // Total shares per option
    uint256[] public totalShares;

    // Reentrancy guard
    bool private locked;

    // Events
    event MarketCreated(address indexed creator, string question, uint256 endTime, string[] options);
    event SharesBought(address indexed buyer, uint8 optionIndex, uint256 amount);
    event SharesSold(address indexed seller, uint8 optionIndex, uint256 sharesAmount, uint256 payout);
    event MarketResolved(uint8[] winnerIndices, uint256 totalPool);
    event MarketCancelled(address indexed cancelledBy);
    event Claimed(address indexed user, uint256 payout);
    event Refunded(address indexed user, uint256 amount);

    // Errors
    error MarketNotActive();
    error MarketEnded();
    error MarketStillOpen();
    error MarketNotResolved();
    error MarketNotCancelled();
    error NotAuthorized();
    error InvalidOption();
    error InvalidAmount();
    error InvalidEndTime();
    error InvalidOptionsCount();
    error EmptyOptionLabel();
    error DuplicateOptionLabel();
    error NoWinnersSpecified();
    error TooManyWinners();
    error DuplicateWinner();
    error NothingToClaim();
    error NothingToRefund();
    error AlreadyClaimed();
    error TransferFailed();
    error ReentrancyGuard();
    error InsufficientShares();
    error InsufficientLiquidity();
    error NotResolver();
    error ResolverAlreadySet();

    modifier nonReentrant() {
        if (locked) revert ReentrancyGuard();
        locked = true;
        _;
        locked = false;
    }

    modifier onlyActive() {
        if (marketStatus != Status.Active) revert MarketNotActive();
        if (block.timestamp >= endTime) revert MarketEnded();
        _;
    }

    modifier onlyCreator() {
        if (msg.sender != creator) revert NotAuthorized();
        _;
    }

    modifier onlyResolver() {
        if (msg.sender != resolver) revert NotResolver();
        _;
    }

    constructor(
        address _creator,
        string memory _question,
        uint256 _endTime,
        string[] memory _options
    ) {
        if (_endTime <= block.timestamp) revert InvalidEndTime();
        if (_creator == address(0)) revert InvalidAmount();
        if (bytes(_question).length == 0) revert InvalidAmount();

        // Validate 2-4 options
        if (_options.length < 2 || _options.length > 4) revert InvalidOptionsCount();

        // Validate each option is non-empty and unique
        for (uint8 i = 0; i < _options.length; i++) {
            if (bytes(_options[i]).length == 0) revert EmptyOptionLabel();
            for (uint8 j = i + 1; j < _options.length; j++) {
                if (keccak256(bytes(_options[i])) == keccak256(bytes(_options[j]))) {
                    revert DuplicateOptionLabel();
                }
            }
        }

        creator = _creator;
        question = _question;
        endTime = _endTime;
        createdAt = block.timestamp;
        marketStatus = Status.Active;

        optionCount = uint8(_options.length);
        optionLabels = _options;
        totalShares = new uint256[](_options.length);
        winningOptions = new bool[](_options.length);

        emit MarketCreated(_creator, _question, _endTime, _options);
    }

    /// @notice Buy shares for a specific option using native MNT
    /// @param optionIndex Index of the option (0 to optionCount-1)
    function buyShares(uint8 optionIndex) external payable onlyActive nonReentrant {
        if (optionIndex >= optionCount) revert InvalidOption();
        if (msg.value == 0) revert InvalidAmount();

        shares[msg.sender][optionIndex] += msg.value;
        totalShares[optionIndex] += msg.value;

        emit SharesBought(msg.sender, optionIndex, msg.value);
    }

    /// @notice Sell shares for a specific option
    /// @param optionIndex Index of the option (0 to optionCount-1)
    /// @param sharesAmount Amount of shares to sell
    function sellShares(uint8 optionIndex, uint256 sharesAmount) external onlyActive nonReentrant {
        if (optionIndex >= optionCount) revert InvalidOption();
        if (sharesAmount == 0) revert InvalidAmount();
        if (shares[msg.sender][optionIndex] < sharesAmount) revert InsufficientShares();

        uint256 totalPool = getTotalPool();
        if (totalPool == 0) revert InsufficientLiquidity();

        // Payout = shares * (optionTotal / totalPool)
        uint256 payout = (sharesAmount * totalShares[optionIndex]) / totalPool;

        if (address(this).balance < payout) revert InsufficientLiquidity();

        // Update state before transfer (CEI pattern)
        shares[msg.sender][optionIndex] -= sharesAmount;
        totalShares[optionIndex] -= sharesAmount;

        (bool success, ) = payable(msg.sender).call{value: payout}("");
        if (!success) revert TransferFailed();

        emit SharesSold(msg.sender, optionIndex, sharesAmount, payout);
    }

    /// @notice Resolve the market with one or more winning options
    /// @param winnerIndices Array of winning option indices
    function resolveMarket(uint8[] calldata winnerIndices) external onlyCreator {
        if (marketStatus != Status.Active) revert MarketNotActive();
        if (block.timestamp < endTime) revert MarketStillOpen();
        if (winnerIndices.length == 0) revert NoWinnersSpecified();
        if (winnerIndices.length > optionCount) revert TooManyWinners();

        // Validate and set winners
        for (uint256 i = 0; i < winnerIndices.length; i++) {
            uint8 idx = winnerIndices[i];
            if (idx >= optionCount) revert InvalidOption();
            if (winningOptions[idx]) revert DuplicateWinner();
            winningOptions[idx] = true;
        }

        hasWinners = true;
        marketStatus = Status.Resolved;

        emit MarketResolved(winnerIndices, getTotalPool());
    }

    /// @notice Cancel the market (only creator, only while active)
    function cancelMarket() external onlyCreator {
        if (marketStatus != Status.Active) revert MarketNotActive();

        marketStatus = Status.Cancelled;

        emit MarketCancelled(msg.sender);
    }

    /// @notice Set the resolver address (can only be set once by creator)
    /// @param _resolver Address of the UMA resolver contract
    function setResolver(address _resolver) external onlyCreator {
        if (resolver != address(0)) revert ResolverAlreadySet();
        if (_resolver == address(0)) revert InvalidAmount();
        resolver = _resolver;
    }

    /// @notice Resolve the market from the UMA resolver
    /// @param winnerIndices Array of winning option indices
    function resolveMarketFromResolver(uint8[] calldata winnerIndices) external onlyResolver {
        if (marketStatus != Status.Active) revert MarketNotActive();
        if (block.timestamp < endTime) revert MarketStillOpen();
        if (winnerIndices.length == 0) revert NoWinnersSpecified();
        if (winnerIndices.length > optionCount) revert TooManyWinners();

        // Validate and set winners
        for (uint256 i = 0; i < winnerIndices.length; i++) {
            uint8 idx = winnerIndices[i];
            if (idx >= optionCount) revert InvalidOption();
            if (winningOptions[idx]) revert DuplicateWinner();
            winningOptions[idx] = true;
        }

        hasWinners = true;
        marketStatus = Status.Resolved;

        emit MarketResolved(winnerIndices, getTotalPool());
    }

    /// @notice Claim winnings after market is resolved (paid in native MNT)
    function claimWinnings() external nonReentrant {
        if (marketStatus != Status.Resolved) revert MarketNotResolved();
        if (hasClaimed[msg.sender]) revert AlreadyClaimed();

        // Calculate user's winning shares and total winning shares
        uint256 userWinningShares = 0;
        uint256 totalWinningShares = 0;

        for (uint8 i = 0; i < optionCount; i++) {
            if (winningOptions[i]) {
                userWinningShares += shares[msg.sender][i];
                totalWinningShares += totalShares[i];
            }
        }

        if (userWinningShares == 0) revert NothingToClaim();

        // Payout = (userWinningShares / totalWinningShares) * totalPool
        uint256 totalPool = getTotalPool();
        uint256 payout = (userWinningShares * totalPool) / totalWinningShares;

        // Mark as claimed and clear shares (CEI pattern)
        hasClaimed[msg.sender] = true;
        for (uint8 i = 0; i < optionCount; i++) {
            shares[msg.sender][i] = 0;
        }

        (bool success, ) = payable(msg.sender).call{value: payout}("");
        if (!success) revert TransferFailed();

        emit Claimed(msg.sender, payout);
    }

    /// @notice Claim refund if market was cancelled (paid in native MNT)
    function claimRefund() external nonReentrant {
        if (marketStatus != Status.Cancelled) revert MarketNotCancelled();

        uint256 refundAmount = 0;
        for (uint8 i = 0; i < optionCount; i++) {
            refundAmount += shares[msg.sender][i];
            shares[msg.sender][i] = 0;
        }

        if (refundAmount == 0) revert NothingToRefund();

        (bool success, ) = payable(msg.sender).call{value: refundAmount}("");
        if (!success) revert TransferFailed();

        emit Refunded(msg.sender, refundAmount);
    }

    // ============ View Functions ============

    /// @notice Get complete market information
    function getMarketInfo() external view returns (
        address _creator,
        string memory _question,
        uint256 _endTime,
        uint256 _createdAt,
        Status _status,
        uint8 _optionCount,
        string[] memory _optionLabels,
        uint256[] memory _totalShares,
        bool[] memory _winningOptions,
        uint256 _totalPool
    ) {
        return (
            creator,
            question,
            endTime,
            createdAt,
            marketStatus,
            optionCount,
            optionLabels,
            totalShares,
            winningOptions,
            getTotalPool()
        );
    }

    /// @notice Get user's position in the market
    function getUserPosition(address user) external view returns (
        uint256[] memory _shares,
        bool _hasClaimed
    ) {
        uint256[] memory userShares = new uint256[](optionCount);
        for (uint8 i = 0; i < optionCount; i++) {
            userShares[i] = shares[user][i];
        }
        return (userShares, hasClaimed[user]);
    }

    /// @notice Get total pool size
    function getTotalPool() public view returns (uint256) {
        uint256 total = 0;
        for (uint8 i = 0; i < optionCount; i++) {
            total += totalShares[i];
        }
        return total;
    }

    /// @notice Get option label by index
    function getOptionLabel(uint8 index) external view returns (string memory) {
        if (index >= optionCount) revert InvalidOption();
        return optionLabels[index];
    }

    /// @notice Get all option labels
    function getOptionLabels() external view returns (string[] memory) {
        return optionLabels;
    }

    /// @notice Get total shares for all options
    function getTotalShares() external view returns (uint256[] memory) {
        return totalShares;
    }

    /// @notice Get winning options array
    function getWinningOptions() external view returns (bool[] memory) {
        return winningOptions;
    }

    /// @notice Calculate potential payout for a user if specific options win
    function calculatePotentialPayout(address user, uint8[] calldata winnerIndices) external view returns (uint256) {
        uint256 totalPool = getTotalPool();
        if (totalPool == 0) return 0;

        uint256 userWinningShares = 0;
        uint256 totalWinningShares = 0;

        for (uint256 i = 0; i < winnerIndices.length; i++) {
            uint8 idx = winnerIndices[i];
            if (idx < optionCount) {
                userWinningShares += shares[user][idx];
                totalWinningShares += totalShares[idx];
            }
        }

        if (totalWinningShares == 0) return 0;
        return (userWinningShares * totalPool) / totalWinningShares;
    }

    /// @notice Get current odds for all options (returns basis points, 10000 = 100%)
    function getOdds() external view returns (uint256[] memory) {
        uint256 totalPool = getTotalPool();
        uint256[] memory odds = new uint256[](optionCount);

        if (totalPool == 0) {
            uint256 equalOdds = 10000 / optionCount;
            for (uint8 i = 0; i < optionCount; i++) {
                odds[i] = equalOdds;
            }
        } else {
            for (uint8 i = 0; i < optionCount; i++) {
                odds[i] = (totalShares[i] * 10000) / totalPool;
            }
        }

        return odds;
    }

    /// @notice Get price for a specific option (basis points, 10000 = 100%)
    function getOptionPrice(uint8 optionIndex) external view returns (uint256) {
        if (optionIndex >= optionCount) revert InvalidOption();
        uint256 totalPool = getTotalPool();
        if (totalPool == 0) return 10000 / optionCount;
        return (totalShares[optionIndex] * 10000) / totalPool;
    }

    /// @notice Check if market is still active and open for betting
    function isOpenForBetting() external view returns (bool) {
        return marketStatus == Status.Active && block.timestamp < endTime;
    }

    /// @notice Get time remaining until market ends (0 if ended)
    function getTimeRemaining() external view returns (uint256) {
        if (block.timestamp >= endTime) return 0;
        return endTime - block.timestamp;
    }

    /// @notice Get contract's MNT balance
    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /// @notice Calculate payout for selling shares at current price
    /// @param optionIndex Index of the option
    /// @param sharesAmount Amount of shares to sell
    /// @return payout Amount of collateral user would receive
    function calculateSellPayout(uint8 optionIndex, uint256 sharesAmount) external view returns (uint256 payout) {
        if (optionIndex >= optionCount) return 0;
        uint256 totalPool = getTotalPool();
        if (totalPool == 0) return 0;
        return (sharesAmount * totalShares[optionIndex]) / totalPool;
    }

    /// @notice Get user's shares for a specific option
    function getUserShares(address user, uint8 optionIndex) external view returns (uint256) {
        if (optionIndex >= optionCount) return 0;
        return shares[user][optionIndex];
    }

    /// @notice Allow contract to receive MNT
    receive() external payable {}
}

contracts/PredictionMarketFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./PredictionMarket.sol";

/// @title PredictionMarketFactory
/// @notice Factory contract for creating prediction markets using native MNT
contract PredictionMarketFactory {
    address public owner;
    address public defaultResolver; // UMA resolver for decentralized resolution

    // All markets created through this factory
    address[] public allMarkets;

    // Markets created by a specific address
    mapping(address => address[]) public marketsByCreator;

    // Track if an address is a valid market
    mapping(address => bool) public isValidMarket;

    event MarketCreated(
        address indexed marketAddress,
        address indexed creator,
        string question,
        uint256 endTime,
        string[] options
    );
    event DefaultResolverUpdated(address indexed newResolver);
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    error NotOwner();
    error InvalidEndTime();
    error InvalidQuestion();
    error InvalidOptionsCount();
    error EmptyOptionLabel();
    error DuplicateOptionLabel();

    modifier onlyOwner() {
        if (msg.sender != owner) revert NotOwner();
        _;
    }

    constructor() {
        owner = msg.sender;
    }

    /// @notice Create a new prediction market with custom options (uses native MNT)
    /// @param _question The prediction question
    /// @param _endTime Unix timestamp when betting closes
    /// @param _options Array of 2-4 option labels
    function createMarket(
        string memory _question,
        uint256 _endTime,
        string[] memory _options
    ) external returns (address marketAddress) {
        if (bytes(_question).length == 0) revert InvalidQuestion();
        if (_endTime <= block.timestamp) revert InvalidEndTime();

        // Validate options count
        if (_options.length < 2 || _options.length > 4) revert InvalidOptionsCount();

        // Validate options are non-empty and unique
        for (uint256 i = 0; i < _options.length; i++) {
            if (bytes(_options[i]).length == 0) revert EmptyOptionLabel();
            for (uint256 j = i + 1; j < _options.length; j++) {
                if (keccak256(bytes(_options[i])) == keccak256(bytes(_options[j]))) {
                    revert DuplicateOptionLabel();
                }
            }
        }

        PredictionMarket newMarket = new PredictionMarket(
            msg.sender,
            _question,
            _endTime,
            _options
        );

        marketAddress = address(newMarket);

        // Set resolver if default resolver is configured
        if (defaultResolver != address(0)) {
            newMarket.setResolver(defaultResolver);
        }

        allMarkets.push(marketAddress);
        marketsByCreator[msg.sender].push(marketAddress);
        isValidMarket[marketAddress] = true;

        emit MarketCreated(
            marketAddress,
            msg.sender,
            _question,
            _endTime,
            _options
        );

        return marketAddress;
    }

    // ============ View Functions ============

    /// @notice Get total number of markets
    function getTotalMarkets() external view returns (uint256) {
        return allMarkets.length;
    }

    /// @notice Get all market addresses
    function getAllMarkets() external view returns (address[] memory) {
        return allMarkets;
    }

    /// @notice Get markets by creator
    function getMarketsByCreator(address creator) external view returns (address[] memory) {
        return marketsByCreator[creator];
    }

    /// @notice Get paginated markets
    function getMarketsPaginated(uint256 offset, uint256 limit)
        external
        view
        returns (address[] memory markets, uint256 total)
    {
        total = allMarkets.length;

        if (offset >= total) {
            return (new address[](0), total);
        }

        uint256 end = offset + limit;
        if (end > total) {
            end = total;
        }

        uint256 resultLength = end - offset;
        markets = new address[](resultLength);

        for (uint256 i = 0; i < resultLength; i++) {
            markets[i] = allMarkets[offset + i];
        }

        return (markets, total);
    }

    /// @notice Get active markets (not resolved or cancelled)
    function getActiveMarkets() external view returns (address[] memory) {
        uint256 activeCount = 0;

        // First pass: count active markets
        for (uint256 i = 0; i < allMarkets.length; i++) {
            PredictionMarket market = PredictionMarket(payable(allMarkets[i]));
            if (market.marketStatus() == PredictionMarket.Status.Active) {
                activeCount++;
            }
        }

        // Second pass: populate array
        address[] memory activeMarkets = new address[](activeCount);
        uint256 index = 0;

        for (uint256 i = 0; i < allMarkets.length; i++) {
            PredictionMarket market = PredictionMarket(payable(allMarkets[i]));
            if (market.marketStatus() == PredictionMarket.Status.Active) {
                activeMarkets[index] = allMarkets[i];
                index++;
            }
        }

        return activeMarkets;
    }

    // ============ Admin Functions ============

    /// @notice Update default resolver (can be set to address(0) to disable)
    function setDefaultResolver(address _resolver) external onlyOwner {
        defaultResolver = _resolver;
        emit DefaultResolverUpdated(_resolver);
    }

    /// @notice Transfer ownership
    function transferOwnership(address newOwner) external onlyOwner {
        if (newOwner == address(0)) revert NotOwner();
        address oldOwner = owner;
        owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }

    // ============ Emergency Admin Functions ============
    // These are needed to fix markets where factory was incorrectly set as creator

    /// @notice Resolve a market where factory is the creator (admin only)
    /// @dev This is a workaround for the bug where createMarketSimple set factory as creator
    /// @param marketAddress The market to resolve
    /// @param winnerIndices Array of winning option indices
    function adminResolveMarket(address payable marketAddress, uint8[] calldata winnerIndices) external onlyOwner {
        require(isValidMarket[marketAddress], "Not a valid market");
        PredictionMarket market = PredictionMarket(marketAddress);
        require(market.creator() == address(this), "Factory is not the creator");
        market.resolveMarket(winnerIndices);
    }

    /// @notice Cancel a market where factory is the creator (admin only)
    /// @param marketAddress The market to cancel
    function adminCancelMarket(address payable marketAddress) external onlyOwner {
        require(isValidMarket[marketAddress], "Not a valid market");
        PredictionMarket market = PredictionMarket(marketAddress);
        require(market.creator() == address(this), "Factory is not the creator");
        market.cancelMarket();
    }
}

contracts/UMAMarketResolver.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./interfaces/IOptimisticOracleV2.sol";

interface IPredictionMarket {
    function endTime() external view returns (uint256);
    function question() external view returns (string memory);
    function optionCount() external view returns (uint8);
    function getOptionLabels() external view returns (string[] memory);
    function marketStatus() external view returns (uint8); // 0 = Active, 1 = Resolved, 2 = Cancelled
    function resolveMarketFromResolver(uint8[] calldata winnerIndices) external;
}

/**
 * @title UMAMarketResolver
 * @notice Resolves prediction markets using UMA's Optimistic Oracle V2
 * @dev Handles oracle requests and settlements for decentralized market resolution
 */
contract UMAMarketResolver {
    using SafeERC20 for IERC20;

    // UMA Optimistic Oracle V2 instance
    IOptimisticOracleV2 public immutable oracle;

    // Bond currency (e.g., WETH, USDC)
    IERC20 public immutable bondCurrency;

    // YES_OR_NO_QUERY identifier for binary questions
    bytes32 public constant IDENTIFIER = bytes32("YES_OR_NO_QUERY");

    // Default liveness period (2 hours for production, can be shorter for testing)
    uint256 public constant DEFAULT_LIVENESS = 7200; // 2 hours

    // Minimum bond amount
    uint256 public constant MIN_BOND = 0;

    // Request tracking
    struct ResolutionRequest {
        address market;
        uint256 requestTime;
        bytes ancillaryData;
        bool resolved;
        uint8 winningOption;
    }

    // Market => request data
    mapping(address => ResolutionRequest) public resolutionRequests;

    // Track which markets have pending requests
    mapping(address => bool) public hasPendingRequest;

    // Events
    event ResolutionRequested(
        address indexed market,
        uint256 requestTime,
        bytes ancillaryData
    );
    event ResolutionProposed(
        address indexed market,
        address indexed proposer,
        int256 proposedPrice
    );
    event MarketResolved(
        address indexed market,
        uint8 winningOption,
        int256 resolvedPrice
    );

    // Errors
    error MarketNotEnded();
    error MarketAlreadyResolved();
    error RequestAlreadyPending();
    error NoRequestPending();
    error RequestNotSettled();
    error InvalidMarket();
    error InvalidProposal();
    error OracleNotSettled();

    /**
     * @notice Constructor
     * @param _oracle Address of UMA Optimistic Oracle V2
     * @param _bondCurrency Address of the bond currency token
     */
    constructor(address _oracle, address _bondCurrency) {
        require(_oracle != address(0), "Invalid oracle address");
        require(_bondCurrency != address(0), "Invalid bond currency");
        oracle = IOptimisticOracleV2(_oracle);
        bondCurrency = IERC20(_bondCurrency);
    }

    /**
     * @notice Request resolution for a prediction market
     * @param market Address of the prediction market to resolve
     * @dev Anyone can call this after market end time
     */
    function requestResolution(address market) external {
        if (market == address(0)) revert InvalidMarket();

        IPredictionMarket pm = IPredictionMarket(market);

        // Check market has ended
        if (block.timestamp < pm.endTime()) revert MarketNotEnded();

        // Check market is still active (not already resolved/cancelled)
        if (pm.marketStatus() != 0) revert MarketAlreadyResolved();

        // Check no pending request
        if (hasPendingRequest[market]) revert RequestAlreadyPending();

        // Build ancillary data from market question and options
        bytes memory ancillaryData = _buildAncillaryData(pm);

        uint256 requestTime = block.timestamp;

        // Store request info
        resolutionRequests[market] = ResolutionRequest({
            market: market,
            requestTime: requestTime,
            ancillaryData: ancillaryData,
            resolved: false,
            winningOption: 0
        });
        hasPendingRequest[market] = true;

        // Request price from oracle (reward = 0, proposer pays bond)
        oracle.requestPrice(
            IDENTIFIER,
            requestTime,
            ancillaryData,
            bondCurrency,
            0 // No reward - proposer earns bond back if correct
        );

        // Set custom liveness period
        oracle.setCustomLiveness(
            IDENTIFIER,
            requestTime,
            ancillaryData,
            DEFAULT_LIVENESS
        );

        emit ResolutionRequested(market, requestTime, ancillaryData);
    }

    /**
     * @notice Settle the oracle request and resolve the market
     * @param market Address of the prediction market
     * @dev Can be called after liveness period has passed
     */
    function settleAndResolve(address market) external {
        if (!hasPendingRequest[market]) revert NoRequestPending();

        ResolutionRequest storage request = resolutionRequests[market];
        if (request.resolved) revert MarketAlreadyResolved();

        // Settle with the oracle
        oracle.settle(
            address(this),
            IDENTIFIER,
            request.requestTime,
            request.ancillaryData
        );

        // Get the resolved price
        IOptimisticOracleV2.Request memory oracleRequest = oracle.getRequest(
            address(this),
            IDENTIFIER,
            request.requestTime,
            request.ancillaryData
        );

        if (!oracleRequest.settled) revert OracleNotSettled();

        int256 resolvedPrice = oracleRequest.resolvedPrice;

        // Convert resolved price to winning option index
        // For binary markets: 1e18 = option 0 wins (YES), 0 = option 1 wins (NO)
        // For multi-option: price represents option index (0, 1e18, 2e18, 3e18)
        uint8 winningOption = _priceToOptionIndex(resolvedPrice, IPredictionMarket(market).optionCount());

        // Mark as resolved
        request.resolved = true;
        request.winningOption = winningOption;
        hasPendingRequest[market] = false;

        // Create winner array and resolve the market
        uint8[] memory winners = new uint8[](1);
        winners[0] = winningOption;

        IPredictionMarket(market).resolveMarketFromResolver(winners);

        emit MarketResolved(market, winningOption, resolvedPrice);
    }

    /**
     * @notice Build ancillary data for oracle request
     * @param pm The prediction market interface
     * @return ancillaryData Formatted ancillary data bytes
     */
    function _buildAncillaryData(IPredictionMarket pm) internal view returns (bytes memory) {
        string memory question = pm.question();
        string[] memory options = pm.getOptionLabels();

        // Format: "Q: <question>? Options: 0:<label0>, 1:<label1>, ... Answer with option index scaled by 1e18 (0 for first option, 1e18 for second, etc.)"
        string memory optionsStr = "";
        for (uint8 i = 0; i < options.length; i++) {
            if (i > 0) {
                optionsStr = string(abi.encodePacked(optionsStr, ", "));
            }
            optionsStr = string(abi.encodePacked(
                optionsStr,
                _uint8ToString(i),
                ":",
                options[i]
            ));
        }

        return bytes(string(abi.encodePacked(
            "Q: ",
            question,
            " Options: ",
            optionsStr,
            ". Answer with the winning option index scaled by 1e18 (0 for option 0, 1000000000000000000 for option 1, etc.). If question is unresolvable, answer with -1e18."
        )));
    }

    /**
     * @notice Convert oracle price to option index
     * @param price The resolved price from oracle
     * @param optionCount Number of options in the market
     * @return Option index (0 to optionCount-1)
     */
    function _priceToOptionIndex(int256 price, uint8 optionCount) internal pure returns (uint8) {
        // Handle unresolvable case (return first option as default, market can handle cancellation)
        if (price < 0) {
            return 0; // Could also trigger cancellation logic
        }

        // Convert from 1e18 scaled value to index
        uint256 index = uint256(price) / 1e18;

        // Clamp to valid range
        if (index >= optionCount) {
            index = optionCount - 1;
        }

        return uint8(index);
    }

    /**
     * @notice Helper to convert uint8 to string
     */
    function _uint8ToString(uint8 value) internal pure returns (string memory) {
        if (value == 0) return "0";
        if (value == 1) return "1";
        if (value == 2) return "2";
        if (value == 3) return "3";
        return "0";
    }

    // ============ View Functions ============

    /**
     * @notice Get resolution request details
     * @param market Address of the prediction market
     */
    function getResolutionRequest(address market)
        external
        view
        returns (
            uint256 requestTime,
            bytes memory ancillaryData,
            bool resolved,
            uint8 winningOption
        )
    {
        ResolutionRequest memory request = resolutionRequests[market];
        return (
            request.requestTime,
            request.ancillaryData,
            request.resolved,
            request.winningOption
        );
    }

    /**
     * @notice Check if a market can be settled
     * @param market Address of the prediction market
     * @return canSettle Whether the market can be settled now
     */
    function canSettle(address market) external view returns (bool) {
        if (!hasPendingRequest[market]) return false;

        ResolutionRequest memory request = resolutionRequests[market];
        if (request.resolved) return false;

        // Check oracle state (6 = Settled, 4 = Expired/ready to settle)
        try oracle.getState(
            address(this),
            IDENTIFIER,
            request.requestTime,
            request.ancillaryData
        ) returns (uint8 state) {
            // State 4 = Expired (can be settled), State 6 = Already settled
            return state >= 4;
        } catch {
            return false;
        }
    }

    /**
     * @notice Get the oracle request state for a market
     * @param market Address of the prediction market
     * @return state The oracle state (0=Invalid, 1=Requested, 2=Proposed, 3=Expired, 4=Disputed, 5=Resolved, 6=Settled)
     */
    function getOracleState(address market) external view returns (uint8) {
        if (!hasPendingRequest[market]) return 0;

        ResolutionRequest memory request = resolutionRequests[market];

        return oracle.getState(
            address(this),
            IDENTIFIER,
            request.requestTime,
            request.ancillaryData
        );
    }

    /**
     * @notice Preview the ancillary data that would be generated for a market
     * @param market Address of the prediction market
     * @return ancillaryData The formatted ancillary data
     */
    function previewAncillaryData(address market) external view returns (bytes memory) {
        return _buildAncillaryData(IPredictionMarket(market));
    }
}

